import React from "react";
import styled from "styled-components";
import GenderSelector from "../buttons/GenderSelector.jsx";
import { StyledButton } from "../buttons/StyledButton.styled.jsx";

// styles

const StyledForm = styled.form`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  gap: 2em;
  padding: 2em;
  font-size: 1rem;
  max-width: 80vw;
  margin: 0 auto;
  @media (min-width: 490px) {
    /* font-size: 1.3rem; */
    max-width: 600px;
  }
  @media (min-width: 810px) {
    font-size: 1.7rem;
    max-width: 1200px;
  }
  @media (min-width: 1248px) {
    &[data-thin="true"] {
      flex-wrap: nowrap;
      flex-direction: column;
      max-width: 9em;
      gap: 0em;
      font-size: 1.4rem;
      padding-block: 0;
      & div {
        flex-direction: row;
      }
    }
  }
  @media (max-width: 1248px) {
    /* max-width: 9em; */
    gap: 0em;
    font-size: 1.4rem;
    padding-block: 0;
    margin-top: 15vh;
  }
  /* & > * {
    outline: 1px dashed pink;
  } */
`;

const FormGroup = styled.div`
  cursor: pointer;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 1em;
  border-radius: 1em;
  max-width: 8em;
  gap: 1em;
  transition: all 0.3s ease-in-out;
  width: 100%;
  aspect-ratio: 1;
  &:focus-within {
    box-shadow: 5px 5px 5px 0px hsl(var(--accent-color) / 0.3);
    background-color: hsl(var(--accent-color) / 0.1);
  }

  @media (max-width: 1248px) {
    gap: 0.5em;
    padding-block: 0.5em;
  }

  /* & > * {
    outline: 1px dashed green;
  } */
`;

const StyledLabel = styled.label`
  width: 100px;
  height: 100px;
  & svg {
    display: block;
    width: 100%;
    height: 100%;
    cursor: pointer;
  }
  &[data-thin="true"] {
    height: 50px;
    width: 50px;
  }
  &[data-thin="true"] > svg {
    height: 50px;
    width: 50px;
  }
`;

const InputRow = styled.div`
  display: flex;
  align-items: baseline;
  color: hsl(var(--accent-color));
  font-size: inherit;
  position: relative;
  border-bottom: 2px solid hsl(var(--accent-color));
  & p {
    font-size: inherit;
  }
  &:has(:invalid) {
    border-bottom: 2px solid hsl(0 100% 50%);
    color: hsl(0 100% 50%);
  }
`;

const StyledInput = styled.input`
  width: 4em;
  font-size: inherit;
  background-color: transparent;
  color: inherit;
  border: none;
  text-align: center;
  &:focus {
    outline: none;
  }
  &:-webkit-autofill,
  &:-webkit-autofill:focus {
    transition: background-color 600000s 0s, color 600000s 0s;
    font-size: inherit;
  }
`;

const CalculateButton = styled(StyledButton)`
  & button {
    display: block;
    height: 100%;
    width: 100%;
    background-color: transparent;
    border: none;
    outline: none;
  }
`;

